C++<pre><code>#include &lt;bits/stdc++.h&gt;
using namespace std;
#define endl '\n'
#define INF 0x3f3f3f3f3f3f3f3f
#define LL long long
const int MAXN = 1e5 + 5;
const double EPS = 1e-9;
int n, m, s, a, b;

vector&lt;vector&lt;int&gt;&gt; g;
vector&lt;int&gt; dep;
vector&lt;vector&lt;int&gt;&gt; fa;
void dfs(int u, int father)
{
    dep[u] = dep[father] + 1;
    fa[u][0] = father;
    for (int i = 1; i &lt;= 22; i++) {
        fa[u][i] = fa[fa[u][i - 1]][i - 1];
    }
    for (auto ele : g[u]) {
        if (ele == father) continue;
        dfs(ele, u);
    }
}
int lca(int u, int v)
{
    if (dep[u] &lt; dep[v]) swap(u, v);
    for (int i = 22; i &gt;= 0; i--) {
        if (dep[fa[u][i]] &gt;= dep[v]) u = fa[u][i];
    }
    if (u == v) return u;
    for (int i = 22; i &gt;= 0; i--) {
        if (fa[u][i] != fa[v][i]) {
            u = fa[u][i];
            v = fa[v][i];
        }
    }
    return fa[u][0];
}
signed main()
{
    ios::sync_with_stdio(false);
    cin.tie(0);
    cin &gt;&gt; n &gt;&gt; m &gt;&gt; s;
    g.assign(n + 1, vector&lt;int&gt;());
    dep.resize(n + 1);
    fa.assign(n + 1, vector&lt;int&gt;(23, 0));
    for (int i = 1; i &lt; n; i++) {
        cin &gt;&gt; a &gt;&gt; b;
        g[a].push_back(b);
        g[b].push_back(a);
    }
    dfs(s, 0);
    while (m--) {
        cin &gt;&gt; a &gt;&gt; b;
        cout &lt;&lt; lca(a, b) &lt;&lt; endl;
    }
    return 0;
}
