C++<pre><code>#include &lt;bits/stdc++.h&gt;
#define int long long
using namespace std;
const int MAXN = 10005;
int n, m, s;
struct edge {
    int u, v, w;
};
vector&lt;edge&gt; e[MAXN];
int dis[MAXN], cnt[MAXN], vis[MAXN];  // cnt最短路经过的边数
queue&lt;int&gt; q;
bool spfa(int n, int s)
{
    for (int i = 1; i &lt;= n; i++) {
        dis[i] = (1ll &lt;&lt; 31) - 1;
    }
    dis[s] = 0;
    vis[s] = 1;
    q.push(s);
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        vis[u] = 0;
        for (auto ele : e[u]) {
            int v = ele.v, w = ele.w;
            if (dis[v] &gt; dis[u] + w) {
                dis[v] = dis[u] + w;
                cnt[v] = cnt[u] + 1;
                if (cnt[v] &gt;= n) return false;
                if (!vis[v]) {
                    q.push(v);
                    vis[v] = 1;
                }
            }
        }
    }
    return true;
}
signed main()
{
    cin &gt;&gt; n &gt;&gt; m &gt;&gt; s;
    for (int i = 1; i &lt;= m; i++) {
        int u, v, w;
        cin &gt;&gt; u &gt;&gt; v &gt;&gt; w;
        e[u].push_back({u, v, w});
    }
    spfa(n, s);
    for (int i = 1; i &lt;= n; i++) {
        cout &lt;&lt; dis[i] &lt;&lt; " ";
    }
    return 0;
}
