C++<pre><code>#include &lt;bits/stdc++.h&gt;
using namespace std;
const int MAXN = 100005;
struct edge {
    int v, w;
};
vector&lt;edge&gt; e[MAXN];
struct Polar {
    int dis, u;
    friend bool operator&gt;(const Polar&amp; a, const Polar&amp; b) { return a.dis &gt; b.dis; }
};
int dis[MAXN], vis[MAXN];
priority_queue&lt;Polar, vector&lt;Polar&gt;, greater&lt;Polar&gt;&gt; q;
void dijkstra(int n, int s)
{
    memset(dis, 0x3f, sizeof(dis));
    memset(vis, 0, sizeof(vis));
    dis[s] = 0;
    q.push({0, s});
    while (!q.empty()) {
        int u = q.top().u;
        q.pop();
        if (vis[u]) continue;
        vis[u] = 1;
        for (auto ele : e[u]) {
            int v = ele.v, w = ele.w;
            if (dis[v] &gt; dis[u] + w) {
                dis[v] = dis[u] + w;
                q.push({dis[v], v});
            }
        }
    }
}
int main()
{
    int n, m, s;
    cin &gt;&gt; n &gt;&gt; m &gt;&gt; s;
    for (int i = 1; i &lt;= m; i++) {
        int u, v, w;
        cin &gt;&gt; u &gt;&gt; v &gt;&gt; w;
        e[u].push_back({v, w});
    }
    dijkstra(n, s);
    for (int i = 1; i &lt;= n; i++) {
        cout &lt;&lt; dis[i] &lt;&lt; " ";
    }
}
</code></pre>
